mapper.select:

SELECT INTEREST_SEQ
FROM TB_INTEREST 
WHERE IN_ID = #{_parameter}


SELECT HASHTAG_SEQ
FROM TB_HASHTAG
WHERE HASHTAG = #{_parameter}


SELECT HASHTAG_SEQ
FROM TB_IF_HASHTAG_INTEREST
WHERE HASHTAG_SEQ = #{HASHTAG_SEQ, jdbcType=DECIMAL} AND INTEREST_SEQ = #{INTEREST_SEQ, jdbcType=DECIMAL}

mapper.insert:

INSERT INTO TB_INTEREST (
	INTEREST_SEQ
	, MIN_COST
	, MAX_COST
	, TRADE_SE
	, IN_ID
	, IN_DT
	, UP_ID
	, UP_DT
) VALUES (
	#{INTEREST_SEQ, jdbcType=DECIMAL}
	, #{price.lower, jdbcType=VARCHAR}
	, #{price.upper, jdbcType=VARCHAR}
	, #{tradeType, jdbcType=VARCHAR}
	, #{userId, jdbcType=VARCHAR}
	, NOW()
	, #{userId, jdbcType=VARCHAR}
	, NOW()
)

mapper.update:

UPDATE TB_INTEREST 
SET
	MIN_COST = #{price.lower, jdbcType=VARCHAR}
	, MAX_COST = #{price.upper, jdbcType=VARCHAR}
	, TRADE_SE = #{tradeType, jdbcType=VARCHAR}
	, UP_ID = #{userId, jdbcType=VARCHAR}
	, UP_DT = NOW()
WHERE IN_ID = #{userId, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM TB_IF_HASHTAG_INTEREST
WHERE HASHTAG_SEQ NOT IN


	#{item}

mapper.select:
SELECT PRODUCT_SEQ, TITLE , MIN_COST, MAX_COST, TRADE_SE , SALE_LOC, SALE_COORDINATE , DELEGATE_FILE_ID, FILE_ID , CONTENT , SALE_SE , IN_ID, IN_DT , UP_ID, UP_DT , SALE_START_DATE, SALE_END_DATE , READ_CNT , INTEREST_CNT , LAST_BUYER_ID FROM TB_PRODUCT
AND MIN_COST >= #{price.lower, jdbcType=DECIMAL}
AND MAX_COST <= #{price.upper, jdbcType=DECIMAL}
AND TRADE_SE = #{tradeType, jdbcType=VARCHAR}
AND SALE_START_DATE >= STR_TO_DATE(#{tradeStartDate, jdbcType=VARCHAR},'%Y-%m-%d')
AND SALE_END_DATE <= STR_TO_DATE(#{tradeEndDate, jdbcType=VARCHAR},'%Y-%m-%d')
