mapper.select:

/* ProductMapper.getMaxProductSeq : 판매품목 시퀀스 조회 */
SELECT MAX(PRODUCT_SEQ)+1 productSeq  /* 상품목록일련번호 */
FROM TB_PRODUCT


/* ProductMapper.getMaxHashtagSeq : 해시태그 시퀀스 조회 */
SELECT MAX(HASHTAG_SEQ)+1 hashtagSeq  /* 해시태그일련번호 */
FROM TB_HASHTAG

mapper.update:

/* updateSellReadCnt : 판매품목 조회수증가 */
UPDATE TB_PRODUCT
SET READ_CNT = READ_CNT+1
WHERE PRODUCT_SEQ = #{PRODUCT_SEQ}

mapper.insert:
		/* ProductMapper.insertSellProduct : 판매품목 등록 */
 		INSERT INTO
 		TB_PRODUCT
 		(
		  PRODUCT_SEQ
		, TITLE
		, MIN_COST
		, MAX_COST
		, TRADE_SE
		, SALE_LOC
		, SALE_COORDINATE
		, DELEGATE_FILE_ID
		, FILE_ID
		, CONTENT
		, SALE_SE
		, IN_ID
		, IN_DT
		, UP_ID
		, UP_DT
		, SALE_START_DATE
		, SALE_END_DATE
		, READ_CNT
		, INTEREST_CNT
		, LAST_BUYER_ID
		)
		VALUES
		(
		  #{productSeq}
		, #{title}
		, #{cash.lower}
		, #{cash.upper}
		, #{tradeSe}
		, #{saleLoc}
		, #{saleCoordinate}
		, #{delegateFileId}
		, #{fileId}
		, #{content}
		, #{saleSe}
		, #{userId}
		, NOW()
		, #{userId}
		, NOW()
		, NOW()
		, #{saleEndDate}
		, 0
		, 0
		, #{lastBuyerId}
		)

mapper.update:
		/* ProductMapper.updateSellProduct : 판매품목 수정 */
 		UPDATE TB_PRODUCT
 		SET TITLE             = #{title}
		  , MIN_COST          = #{cash.lower}
		  , MAX_COST          = #{cash.upper}
		  , TRADE_SE          = #{tradeSe}
		  , SALE_LOC          = #{saleLoc}
		  , SALE_COORDINATE   = #{saleCoordinate}
		  , DELEGATE_FILE_ID  = #{delegateFileId}
		  , FILE_ID           = #{fileId}
		  , CONTENT           = #{content}
		  , SALE_SE           = #{saleSe}
		  , UP_ID             = #{userId}
		  , UP_DT             = NOW()


		WHERE PRODUCT_SEQ = #{productSeq}

mapper.insert:

/* insertHashtag : 해시태그등록 */
INSERT INTO 
TB_HASHTAG
(
  HASHTAG_SEQ
, HASHTAG
)
VALUES
(
  #{hashtagSeq}
, #{hashtag}
)


/* insertHashtagProduct : 해시태그상품등록 */
INSERT INTO 
TB_IF_HASHTAG_PRODUCT
(
  HASHTAG_SEQ
, PRODUCT_SEQ
)
VALUES
(
  #{hashtagSeq}
, #{productSeq}
)

mapper.delete:

	/* deleteHashtag : 해시태그삭제 */
	DELETE FROM
	TB_HASHTAG
	WHERE HASHTAG_SEQ IN
	(
     SELECT HASHTAG_SEQ 
	 FROM TB_IF_HASHTAG_PRODUCT
	 WHERE PRODUCT_SEQ = #{productSeq}
	)


/* deleteHashtagProduct : 해시태그상품삭제 */
DELETE FROM
TB_IF_HASHTAG_PRODUCT
WHERE PRODUCT_SEQ = #{productSeq}
